mapper.select:
SELECT program.* from projects program left join program_projects pp on program.id = pp.program_id and pp.deleted_at = '1970-01-01 00:00:00' left join project_members pm on program.id = pm.project_id and pm.deleted_at = '1970-01-01 00:00:00' left join project_recent_views prv on pm.project_id = prv.project_id and pm.user_id = prv.user_id and prv.deleted_at ='1970-01-01 00:00:00' where (program.deleted_at = '1970-01-01 00:00:00' or program.deleted_at = '1990-01-01 00:00:00' ) and program.team_owner_id = #{teamId} and program.pm_type = 1
and pp.project_id = #{projectId}
and pm.user_id = #{userId}
group by program.id order by prv.updated_at desc, program.id desc

select p.* from projects p inner join program_projects pp on pp.project_id = p.id and pp.deleted_at = '1970-01-01 00:00:00' left join projects program on program.id = pp.program_id and (program.deleted_at = '1970-01-01 00:00:00' or program.deleted_at = '1990-01-01 00:00:00') left join project_members pm on pm.project_id = p.id and (pm.deleted_at = '1970-01-01 00:00:00' or pm.deleted_at = '1990-01-01 00:00:00') where (p.deleted_at = '1970-01-01 00:00:00' or p.deleted_at = '1990-01-01 00:00:00') and p.team_owner_id = #{teamId} and p.pm_type = 0 and p.invisible = 0
and program.id = #{programId}
and pm.user_id = #{userId}
group by p.id order by pp.created_at asc
